    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try{

            //jOptionPane1.showMessageDialog(null, "Plačnik in prejemnik sta enaka","Napaka",jOptionPane1.ERROR_MESSAGE);
            br=new BufferedReader(new FileReader("racuni.txt"));
            pw=new PrintWriter(new FileWriter("racuni_temp.txt"));
            pol=  new PrintWriter(new FileOutputStream(new File("poloznica.txt"), true)); 
            String tab[]=null;
            String vrstica="";
            int tmp=Integer.parseInt(znesek.getText());      //prenos vrednosti iz polja na obrazcu v spremenljivko
            boolean kontrola=false;
            String iban=ibanPla.getText();
            String ref=ref1Pla.getText()+ref2Pla.getText();
            String nazivPl=nazivPla.getText();
            String naslovPl=naslovPla.getText();
            String krajPl=krajPla.getText();
            String drzavaPl=drzavaPla.getSelectedItem().toString();
            String kodaN=kodaNamena.getSelectedItem().toString();
            String namenP=namenPlacila.getText();
            String nujnoP;
            if(nujno.isSelected()== true)
                nujnoP="JE nujno";
            else
                nujnoP="NI nujno";
            double znesekP=Double.parseDouble(znesek.getText());
            String datumPl=datumPlacila.getDate().toString();
            String bicPl=bic.getText();
            String ibanPl=ibanPre.getText();
            String refPre=ref1Pre.getText()+ref2Pre.getText();
            String nazivPr=nazivPre.getText();
            String naslovPr=naslovPre.getText();
            String krajPr=krajPre.getText();
            String drzavaPr=drzavaPre.getSelectedItem().toString();
            
            while(br.ready()){
                vrstica=br.readLine();
                tab=vrstica.split(" ");       //iz niza nam vsebino prenese v tabelo
                if(Login.oseba.equals(tab[0])){
                    if(Integer.parseInt(tab[9])-tmp>=0){
                        pw.println(tab[0]+" "+tab[1]+" "+tab[2]+" "+tab[3]+" "+tab[4]+" "+tab[5]+" "+tab[6]+" "+tab[7]+" "+tab[8]+" "+(Integer.parseInt(tab[9])-tmp)); //zapiši nov znesek
                        pol.println(iban+" "+ref+" "+nazivPl+" "+naslovPl+" "+krajPl+" "+drzavaPl+" "+kodaN+" "+namenP+" "+nujnoP+" "+znesekP+" "+datumPl+" "+bicPl+" "+ibanPl+" "+
                                refPre+" "+nazivPr+" "+naslovPr+" "+krajPr+" "+drzavaPr);
                        kontrola=true;
                    }else
                        pw.println(vrstica); //zapiši vrstica
                }else{
                    pw.println(vrstica); //zapiši vrstica
                }                                                
            }
            
            if(kontrola==false)
                jOptionPane1.showMessageDialog(null, "Niste prijavljeni","Napaka",jOptionPane1.ERROR_MESSAGE);
            
            br.close();
            pw.close();
            pol.close();

            File original=new File("racuni.txt");
            original.delete();
            new File("racuni_temp.txt").renameTo(original);

            if(kontrola)
            jOptionPane1.showMessageDialog(null,
                "Transakcija je zaključena."+"Račun je plačan",
                "Napaka",jOptionPane1.INFORMATION_MESSAGE);

            znesek.setText("");

        }catch(Exception e){
            jOptionPane1.showMessageDialog(null, "Error","Napaka",jOptionPane1.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

